// css imports
import './Login.css'

import { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'

import { auth, logInWithEmailAndPassword, logout } from '../../config/firebase-config';

import { message, notification } from "antd";

const Login = () => {
  const [loginEmail, setLoginEmail] = useState();
  const [loginPwd, setLoginPwd] = useState();
  const [incorrect, setIncorrect] = useState();

  const errorMessage = (text) => {
    message.error(text)
  }

  const validation = () => {
    if (!loginEmail || loginEmail == '') { errorMessage('please enter your email'); return false; }
    else if (!loginPwd || loginPwd == '') { errorMessage('please enter your password'); return false; }
  }

  const handleLogin = () => {
    if (!validation()) {
      return;
    } else {
      logInWithEmailAndPassword(loginEmail, loginPwd, setIncorrect)
    }
  }

  useEffect(() => {
  }, [loginEmail, loginPwd])

  return (
    <>
      <div className="register-container">
        <div className="register">
          <div className="register-card">
            <Link to="/"><img src={require('../../assets/fish_icon.png')} alt='logo' className='register-fish'/></Link>
            <div className="register__title-container">
              <h1 className="register__title">ログイン</h1>
            </div>
            <div className="register-form">
              <div className="register-form__email-container">
                <input className='register-form__textBox-full' type="email" name="email" required={true} onChange={(e) => { setLoginEmail(e.target.value); }} placeholder="Email"></input>
              </div>
              <div className="register-form__password-container">
                <input className='register-form__textBox-full' type="password" required onChange={(e) => { setLoginPwd(e.target.value); }} placeholder="Password"></input>
              </div>
              <div className="register-form__btn-container">
                <button className="register-form__btn" onClick={handleLogin}>Login</button>
                <Link to="/register" >Don't have an account? Sign up.</Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Login
